const chatRoomTypesOption = [
   'group',
   'personal',
]


module.exports.formatPhone = (currentPhone) => {
    let list_number = '1234567890'
    let phone = currentPhone.split('');
    phone = phone.filter(data => list_number.includes(data));
    phone[0] == '0' ? phone[0] = '62' : '';
    return phone.join('');
}

module.exports.chatRoomTypesValidation = (type) => {
    type !== null ? type = type.toLowerCase().trim() : '';

    if (!activityTypesOption.includes(type)){
        return 'err'
    } 

    return type
}

