module.exports.generate = async (event, code, data) => {
    if (process.env.STAGE === "dev") {
        return {
            statusCode: code,
            headers: {
                'Access-Control-Allow-Origin': '*',
                'Access-Control-Allow-Credentials': true,
            },
            body: JSON.stringify(
                {
                    message: data,
                    input: event,
                },
                null,
                2
            ),
        };
    }

    return {
        statusCode: code,
        headers: {
            'Access-Control-Allow-Origin': '*',
            'Access-Control-Allow-Credentials': true,
        },
        body: JSON.stringify(
            {
                message: data,
            },
            null,
            2
        ),
    };

}